	outline: none;
}
.Vehicle-Search-Single{
	.contentWrap{
		margin: 0 -15px;
	}
	margin: 60px 0 0 0; float: left; width: 100%;
	.Left, .Right{
		width: 50%; float: left; padding: 0 15px;
	}
	.Left{
		.ImageWrap{
			background-color: #f6f6f6;
			text-align: center;
			float: left; 
			width: 100%;
			.ImageMn {
			    max-width: 400px; 
			    height: 240px;
			    margin: 30px auto 20px auto;
			    position: relative;
			    img{
			    	position: absolute;
			    	top: 50%;
			    	left: 50%;
			    	max-height: 100%;
			    	@include translate(-50%,-50%);
			    }
			}
			.LeftContent{
				span{
					font-size: 18px;
					font-weight: normal;
					line-height: 28px;
					letter-spacing: normal;
					color: #3a3839;
				}
				h3{
					font-size: 28px;
					font-weight: 800;
					line-height: 36px;
					letter-spacing: normal;
					text-align: center;
					color: #2b2b2b;
					margin: 0 0 30px 0; 
				}
			}
		}
	}
	.Right{
		.toptxt{
			font-size: 20px;
			font-weight: 800;
			line-height: 28px;
			letter-spacing: normal;
			color: #242424;
			margin: 0 0 10px 0;
			text-transform: uppercase; 
		}
		h3{
			font-size: 42px;
			font-weight: 800;
			line-height: 50px;
			letter-spacing: normal;
			color: #2b2b2b;
			margin: 0 0 10px 0;
			text-transform: uppercase; 
		}
		.titlebottom{
			font-size: 18px;
			font-weight: normal;
			line-height: 28px;
			letter-spacing: normal;
			color: #3a3839;
			margin: 0 0 15px 0;
		}
	}
	.size_result_mn{
		.tabs{
			border: 0;
			.tab{
				border:1px solid #231f20;
				.tab-title{
					font-size: 18px;
					font-weight: 800;
					line-height: 28px;
					letter-spacing: normal;
					color: #2b2b2b;
					margin: 0;
					text-transform: uppercase;padding: 11px 12px;
				}
				&.is-active{
					margin: -4px 0 0;
				&:after{
					content: "";
				    background: #231f20;
				    height: 4px;
				    width: 2px;
				    display: inline-block;
				    position: absolute;
				    left: 100%;
				    top: -1px;
				}
				.tab-title{
					background:#231f20;color: #fff;padding: 13px 12px;
				}
			}
			&:nth-child(2){
				&.is-active{
					&:after{
						right:100%;left: auto;
					}
				}
			}
			}
		}
		.tabs-contents{	
			border: solid 5px #231f20;max-width: 496px;padding: 30px; background: #f6f6f6;
			.tab-content{
				padding: 0;
				>ul{
				margin: 0; list-style: none;
				>li{
					margin: 0 0 15px 0;
					display: inline-block;
					a{
					    width: 426px;
	    				height: 60px;
	    				border-radius: 0px;
	    				background-color: #fff;
						line-height: 60px;
						display: inline-block;					
						font-size: 20px;
						font-weight: 700;
						letter-spacing: normal; 
						color: #3a3839;
						text-transform: uppercase;
						transition: all 0.5s ease;
						border: solid 1px #3a3839;	
	                    &:hover{
	    					background: #ececec;
	            			transition: all 0.5s ease;
	            			.weels.weels{
	            				background: #d99214;transition: all 0.5s ease;  
	            			}
	    				}
	    				
						&:after{
							content: "";
							font-family: "Font Awesome 5 Free";
							font-size: 26px;
							font-weight: normal;
							letter-spacing: normal;
							float: right;
							margin: 0 24px 0 0;
							color: #d3d3d3;
						}

						.weels , .front, .rear{
							float: left;
							font-size: 12px;
							line-height: 18px;
							margin: 6px 0 0 16px;
							span{
								font-size: 26px;
								line-height: 30px;
								display: block;
								margin-left: 5px;
							}
							+span{
								margin: 0 0 0 35px;
								&.front{
							        margin: 6px 0 0 16px;
							        span{ 
	                                    font-size: 18px;
	                                    font-weight: 700;
	                                    line-height: 28px;
							        }
								}
								&.rear{
						            margin:6px 0 0 29px;
	    					        span{ 
	                                    font-size: 18px;
	                                    font-weight: 700;
	                                    line-height: 28px;
	    					        }
								} 
							}
							&.weels{
								background:#efa41f;
								margin: 0;
								padding:6px 28px 4px 16px;
								color: #fff;
							}
						}
					}
					>ul{
					    margin: 5px 0 0 22px;
					    list-style: none; 
					    border-left: 6px solid #d3d3d3; 
					    padding: 0 0 0 22px; 
					    >li{
	                        margin: 0 0 10px 0;
	                        display: inline-block;
	                        
	        				a{
	        				    width: 376px;
	                            height: 40px;
	                            border-radius: 0px;
	                            background-color: #fff;
	        					line-height: 40px; 
	        					display: inline-block;					
	        					font-size: 18px;
	        					font-weight: 700;
	        					letter-spacing: normal;
	        					color: #3a3839;
	        					text-transform: uppercase;
	        					transition: all 0.5s ease;
	        					padding: 0;
	        					>span{
	        					    margin: 0 22px 0 0; 
	        					    text-transform: capitalize;
	        					    font-size: 16px;
	        					    line-height: 24px; 
	        					    background:#efa41f;
	        					    color: #fff;
	        					    padding: 9px 0px 5px 15px;
	        					    width: 115px;
	        					    display: inline-block;
	        					    +span{
	        					        margin: 0;padding:0;
	        					        background: #fff;
	        					        color:#3a3839;
	        					        font-size: 18px;
	        					        width: auto;
	        					    } 
	        					}
	        					
	                            &:hover{
	            					background: #ececec;
	            					transition: all 0.5s ease;
	            					span{
	            						background: #d99214;transition: all 0.5s ease;
	            						+span{
	            							background: #ececec;transition: all 0.5s ease;
	            						}
	            					}
	            				}
	            				 
	        					&:after{
	        						content: "";
	        						font-family: "Font Awesome 5 Free";
	        						font-size: 26px;
	        						font-weight: normal;
	        						letter-spacing: normal;
	        						float: right;
	        						margin: 0 24px 0 0;
	        						color: #d3d3d3;
	        					}
	        				}
	            			+li{
	    				        margin: 0; 
	    				    } 
					    }
					}
                    &:nth-child(n+2){
                        margin-top: 15px;
                        position: relative;
                        margin-bottom: 15px;
                        &:before{
                        	content: "";
                        	background: #dadada;
                        	width: 100%;
                        	height: 1px;
                        	position: absolute;
                        	top: -15px;
                        }
                    }
                    &:last-child{
                        margin-bottom: 0px;
                    }
				    } 
				}
			+.tab-content{
				>ul{
					>li{
						a{
							.weels{
								padding: 6px 15px 4px 16px;
								+span{
								margin: 0 0 0 29px;
								}
							}
							&:after{
								margin: 0 14px 0 0;
							}

						}
					}
				}
			}
			} 
		}
	}
	.Important-Note{
		margin: 15px 0 0 0;
		background-color: #f6f6f6;
		padding: 15px;
		h5{
			font-size: 14px;
			font-weight: bold;
			line-height: 20px;
			letter-spacing: normal;
			color: #4295f3;
			text-transform: uppercase;
			margin: 0px 0px 5px 0px;
			padding: 0 0 0 22px;
			position: relative;

			&:before{
				content: "";
				font-family: "Font Awesome 5 Free";
				font-size: 12px;
				color: #4295f3;
				position: absolute;
				left: 0;
			}
		}
		p{
			margin: 0;
			padding: 0 0 0 22px;
			font-size: 12px;
			font-weight: normal;
			line-height: 18px;
			letter-spacing: normal;
			color: #242424;
		}
	}
	.Need-Help{
		margin: 30px 0 0 0;
		>a{
			font-size: 20px;
			font-weight: 800;
			line-height: 28px;
			letter-spacing: normal;
			color: #3a3839;
			&:after{
				content: "";
				font-family: "Font Awesome 5 Free";
				font-size: 20px;
				color: #313131;
				margin: 0 0 0 11px;
			}
			&.is-open{
				&:after{
					content: "";
				}
			}
		}
		.Need-Help-Content{
			background-color: #f6f6f6;
			padding: 15px 15px 0 15px;
			margin: 11px 0 0 0;
			text-align: center;
			p{
				font-size: 18px;
				font-weight: normal;
				line-height: 28px;
				letter-spacing: normal;
				color: #242424;
				margin: 0 0 15px 0;
				text-align: left;
			}
			img{
			    max-width: 424px;
			}
		}
	}
	.bottomDiv{
		float: left;
		width: 100%;
		text-align: center;
		margin: 60px 0;
		a{
			width: 399px;
			height: 60px;
			line-height: 60px;
			border-radius: 0px;
			border: solid 1px #3a3839;
			font-size: 24px;
			font-weight: 800;
			letter-spacing: normal;
			color: #232323;
			text-transform: uppercase;
			display: inline-block;
			transition: all 0.5s ease;

			&:hover{
				transition: all 0.5s ease;
				background: #ececec;
			}
		}
	}
}

