cmspage.scss:
/*help-center*/

.page#cms-block .page-heading {
    display: none !important;
}

.page#cms-block p {
    margin: 0 0 10px 0;
    line-height: 1.56;
} 

.page.Cms-Content#cms-block .page-content--centered {
    max-width: 100%;
    padding: 0;
}

.top-help-banner {
    display: block;
    width: 100%; 
    line-height: 1; 
    vertical-align: top;
}

.rightside.imgblock {
    position: absolute;
    right: 0;
}

.rightside.imgblock:before {
    content: "";
    height: 100%;
    width: 200px;
    position: absolute;
    left: 0;
    right: auto;
    top: 0;
    background-image: linear-gradient(to right, #fff, rgba(255, 255, 255, .8) 44%, rgba(255, 255, 255, 0));
}

.contentblock {
    width: 100%;
    display: block;
    position: relative;
    z-index: 1;
    padding: 30px 0 0;
    max-width: 770px;
    margin-bottom: 57px;
}

.contentblock h1 {
    display: block;
    font-size: 42px;
    font-weight: 900;
    color: #0a4e66;
    margin: 0 0 10px;
    line-height: 1.19;
    text-transform: uppercase;
    text-align: left;
}

.page#cms-block .contentblock .help-content p {
    display: block;
    font-size: 18px;
    font-weight: 400;
    line-height: 1.56;
    margin: 0px 0px 15px;
    color: #242424;
}

.pages-listing {
    background-color: #f4f7f8;
    position: relative;
    float: left;
    width: 100%;
}

.page-list {
    width: auto;
    display: block;
    margin: 45px -15px;
}

.page-list:after,
.tires-size-details-block:after,
.bother-measur-block:after,
.signs-blocks:after,
.tire-sets-box-block:after{
    content: "";
    display: block;
    clear: both;
}

.page-list .item {
    display: inline-block;
    width: 33.33%;
    padding: 15px;
    float: left;
}

.page-contant {
    display: inline-block;
    width: 100%;
    padding: 0 30px 30px 30px;
    min-height: 246px;
    position: relative;
    background-color: #fff;
    border: 1px solid #d5dcde;
    border-radius: 0px 0px 4px 4px;
    border-top: 0px;
}

.page-contant h2 {
    display: block;
    line-height: 1.3;
    vertical-align: top;
    font-size: 20px;
    font-weight: bold;
    color: #0a4e66;
    margin: 20px 0 10px 0;
}

.page-img {
    display: block;
    vertical-align: top;
    line-height: 1;
}

.page-img img {
    width: 100%;
    height: auto;
}

.item .page-contant p {
    display: block;
    line-height: 1.5;
    vertical-align: top;
    font-size: 16px;
    font-weight: normal;
    color: #242424;
    margin-bottom: 10px;
}

.item .page-contant a {
    display: block;
    line-height: 18px;
    font-size: 12px;
    font-weight: 800;
    border: 1px solid #d3d3d3;
    border-radius: 50px;
    padding: 5px 0;
    color: #242424;
    text-align: center;
    position: absolute;
    bottom: 30px;
    width: auto;
    left: 30px;
    right: 30px;
    text-transform: uppercase;
}

.item .page-contant a:hover {
    background: rgba(10, 78, 102, .13);
    border: 1px solid #0a4e66;
    transition: all .5s ease;
}

.policy-block {
    display: block;
    float: left;
    width: 100%;
    margin: 60px 0 20px 0;
}

.policy-block .policy-container {
    max-width: 998px;
    padding: 0 15px;
    margin: 0 auto;
}

.policy-block .policy-container h2 {
    display: block;
    vertical-align: top;
    line-height: 1.17;
    font-size: 36px;
    font-weight: 900;
    margin: 0 0 20px 0;
    color: #0a4e66;
    text-align: center;
}

.policy-block .policy-container .policy-list-block {
    display: block;
    vertical-align: top;
    line-height: 1;
    list-style: none;
    margin: 0px -12px;
}

.policy-block .policy-container .policy-list-block .policy-btn {
    padding: 10px 12px;
    display: inline-block;
    width: 25%;
    float: left;   
}

.policy-block .policy-container .policy-list-block .policy-btn a {
    display: block;
    vertical-align: top;
    width: 100%;
    text-align: center;
    border: 1px solid rgba(10, 78, 102, .51);
    padding: 8px 0 6px;
    font-size: 16px;
    border-radius: 20px;
    color: #0a4e66;
    font-weight: bold;
    background: #fff;
    transition: all .5s ease;
    line-height: 1.5;
}

.policy-block .policy-container .policy-list-block .policy-btn a:hover {
    background: rgba(10, 78, 102, .13);
    border: 1px solid #0a4e66;
    transition: all .5s ease;
}


/*HOW TO SIZE YOUR TIRES*/

.top-size-your-tir-banner {
