    position: absolute;
    left: 0;
    right: auto;
    top: 0;
    background-image: linear-gradient(to right, #fff, rgba(255, 255, 255, .8) 44%, rgba(255, 255, 255, 0));
}

.rightside.imgblock:after {
    content: "";
    background-image: linear-gradient(to top, #fff, rgba(255, 255, 255, .8) 44%, rgba(255, 255, 255, 0));
    height: 200px;
    width: auto;
    position: absolute;
    top: auto;
    left: 0;
    right: 0;
    bottom: 0;
}

