    display: block;
    line-height: 1.17;
    vertical-align: top;
    font-size: 36px;
    font-weight: 900;
    color: #0a4e66;
    margin: 0px 0 22.5px 0;
    text-align: center;
    text-transform: uppercase;
}

.educational-topics {
    width: auto;
    margin: 0px -15px 0px;
}

.topics-items {
   width: 33.33%;
   float: left;
   padding: 7.5px 15px;
}
.topics-items .item {
    display: block;
}
.topics-items .item .topics {
    display: block;
}

.topics-items .item .topics .topics-links {
    display: block;
    line-height: 26px;
    vertical-align: top;
    font-size: 20px;
    font-weight: bold;
    color: #0a4e66;
    text-align: left;
    background-color: #fff;
    border: 1px solid #d5dcde;
    border-radius: 4px;
    padding: 16px 0 16px 20px;
}

.topics-items .item .topics .topics-links.active,
.topics-items .item .topics .topics-links:hover{
    display: block;
    border-left: 5px solid #1a9eca;
    color: #1a9eca;
    transition: all .5s ease;
