custom.scss:
@import url('https://use.fontawesome.com/releases/v5.8.2/css/solid.css');
@import url('https://use.fontawesome.com/releases/v5.8.2/css/brands.css');
@import url('https://use.fontawesome.com/releases/v5.8.2/css/fontawesome.css');
$deepbluecolor:#2b2b2b;
$whitecolor:#fff;

$onecolumn:100%;
$secondcolumn:50%;
$threecolumn:33.33%;
$fourcolumn:25%;
$fivecolumn:20%;
$sixcolumn:16.66%;
$break-sticky: 991px;

// generic transform  
@mixin transform($transforms) {
	-moz-transform: $transforms;        
	-o-transform: $transforms;
	-ms-transform: $transforms; 
	-webkit-transform: $transforms;        
	transform: $transforms;  
}

// rotate
@mixin rotate ($deg) {
	@include transform(rotate(#{$deg}deg));          
}

// scale  
@mixin scale($scale) {
	@include transform(scale($scale));
}

// translate
@mixin translate ($x, $y) {
	@include transform(translate($x, $y));
}

// skew
@mixin skew ($x, $y) {
	@include transform(skew(#{$x}deg, #{$y}deg));
}

@mixin normalFont16 {
	font-size: 16px;
	font-weight: normal;
	letter-spacing: normal;
}

@mixin commonh3 {
	font-size: 24px;
	font-weight: 800;
	color: $deepbluecolor;
	line-height: 32px;
	display: inline-block;
}

@mixin transition($transition...) {
	-moz-transition: $transition;
	-o-transition: $transition;
	-webkit-transition: $transition;
	transition: $transition;
}

@mixin transition-property($property...) {
	-moz-transition-property: $property;
	-o-transition-property: $property;
	-webkit-transition-property: $property;
	transition-property: $property;
}

@mixin transition-duration($duration...) {
	-moz-transition-property: $duration;
	-o-transition-property: $duration;
	-webkit-transition-property: $duration;
	transition-property: $duration;
}

@mixin transition-timing-function($timing...) {
	-moz-transition-timing-function: $timing;
	-o-transition-timing-function: $timing;
	-webkit-transition-timing-function: $timing;
	transition-timing-function: $timing;
}

@mixin transition-delay($delay...) {
	-moz-transition-delay: $delay;
	-o-transition-delay: $delay;
	-webkit-transition-delay: $delay;
	transition-delay: $delay;
}
@mixin ROW{
	float: left;width:100%;
}

.menuUl {
	margin: 0 -15px;
	position: relative;
	padding: 30px 30px 0px;
}

.menuUlLi {
	width: 22.2%;
	padding: 0 15px;
	float: left;
}

.menuUlLiSpan {
	@include normalFont16;
	line-height: 24px;
	color: #3a3839;
	display: block;
}

.menuUlLiSpanA {
	@include normalFont16;
	line-height: 20px;
	width: 100%;
	color: #3a3839;
	border-radius: 0px;
	border: 2px solid #3a3839;
	width: 100%;
	float: left;
	padding: 11px 15px;
}

.menuUlLiSpanAI {
	float: right;
}

.menuUlLiSpanAis_open {
	border-radius: 0px;
	border-bottom: 0;
	padding: 11px 15px 32px; 
	border-color: #efa41f;
	position: relative;
}

.menuUlLiSpanAis_openI {
	@include rotate(180);
}

.menuUlLiSpanAafter {
	content: "";
	background: #fff;
	height: 2px;
	width: 100%;
	display: inline-block;
	position: absolute;
	bottom: -2px;
	left: 0;
	right: 0;
	z-index: 3;
}

.navPage-subMenuis_open {
	z-index: 2;
	box-shadow: none;
	background-color: $whitecolor;
	padding: 17px 0 25px;
	border: 2px solid #efa41f;
	left: 0;
	right: 0;
	width: auto;
	margin: 0 15px;
	top: 100%;
}

.navPage-subMenuis_openul {
	margin: 15px -25px 0px;
	width: auto;
}

.navPage-subMenuis_openulli {
	width: 20%;
	float: left;
	padding: 0 25px;
}

.navPage-subMenuis_openullia {
	padding: 3px 0 3px 5px;
	margin-bottom: 5px;
	display: inline-block;
}

.itemsList ul {
	margin: 15px 0px 0px;
	width: 20%;
	list-style: none;
	float: left;
	padding: 0 25px;
}

.itemsList ul li {
	width: 100%;
}

.itemsList ul li a {
	padding: 3px 0 3px 5px;
	margin-bottom: 5px;
	display: inline-block;
	font-size: 16px;
	line-height: 24px;
}

body {
	font-family: "multi-display";
	float: left;
	width: 100%;

	@media screen and (min-width: $break-sticky) {
		padding-top: 170px;
	}

	h1,
	h2,
	h3,
	h4,
	h5,
	h6 {
		font-family: "multi-display";
	}

	a {
		text-decoration: none;
	}

	.button {
		font-family: "multi-display";
	}
}

* {
	outline: none;
}

h1,
h2,
h3,
h4,
h5,
h6 {
	letter-spacing: normal;
}

.body {
	margin-top: 0;
}

.is-open-custom {
	display: block;
}

.header {
	float: left;
	border: 0;

	.navUser {
		background-color: #f6f6f6; 
		height: 41px;

		.navUser-section--left,
		.navUser-section--right {
			float: right;

			.navUser-item {
				line-height: 41px;
				margin-right: 30px; 
			}

			i,
			.navUser-action {
				display: inline-block;
				font-size: 14px;
				color: #3a3839;
			}

			.navUser-action {
				padding: 0;
				font-weight: normal;
				letter-spacing: normal;
				line-height: 1.29;
				margin-left: 10px;
				text-transform: none;

				&:hover {
					text-decoration: underline;
				}

				&+.navUser-action {
					margin: 0;
				}
			}

			.navUser-item--call {
				.navUser-action {
					font-weight: 900;
					text-transform: uppercase;
					color: #2b2b2b;
				}
			}
		}

		.navUser-section--right {
			float: right;

			.navUser-item {
				margin: 0;
			}
		}
	}

	.HeaderWrap {
		max-width: 1170px;
		width: 100%;
		margin: 0 auto;
        clear: both;
		.Headerleft {
			float: left;
			width: 25%;
			padding-right: 10px;

			.header-logo {
				margin: 41px 0 48px 0;
				float: left;
				width: auto;
				font-family: "multi-display";
				text-align: left;
				a {
					display: inline-block;
					width: auto;

					img {
						max-width: 282px;
					}
				}

				span {
					font-size: 13px;
					font-weight: 800;
					font-style: normal;
					font-stretch: normal;
					line-height: 1.08;
					letter-spacing: normal;
					color: #afafaf;
					display: block;
				}
			}
		}

		.HeaderRiht {
			float: right;
			float: right;
			width: 75%;
			padding-left: 26px;

			.MenuWrap {
				display: inline-block;
				vertical-align: middle;
				margin: 15px 0px 0px 0px;
				width: 77%;
				>nav {
					>ul {
						>li {
							list-style: none;
							display: inline-block;
							margin: 0;

							>a {
								font-size: 18px;
								font-weight: 700;
								font-style: normal;
								font-stretch: normal;
								line-height: 28px;
								letter-spacing: normal;
								color: #3a3839;
								text-transform: uppercase;
								text-decoration: none;
								display: inline-block;
								padding: 13px 15px 15px;

								i {
									margin-left: 5px;
									font-size: 14px;
									color: #3a3839;
									display: inline-block;
								}

								&.is-open {
									background: #231f20;
									color: $whitecolor;

									i {
										color: $whitecolor;
										@include rotate(180);
									}
								}
							}

							&:last-child {
								margin-right: 0;
							}

							.navPage-subMenu.is-open {
								z-index: 1;
								box-shadow: 0 8px 9px -2px rgba(0, 0, 0, 0.5);
								background-color: #231f20;
								padding: 30px 30px 80px;
							}

							.menu_inner {
								max-width: 1170px;
								margin: 0 auto;
								background: $whitecolor;

								>ul {
									background: #535051;
									border: 0px;

									li {
										border: 0px;

										a {
											color: $whitecolor;
											font-size: 18px;
											font-weight: normal;
											font-style: normal;
											font-stretch: normal;
											line-height: 28px;
											letter-spacing: normal;
											padding: 10px 35px 8px 40px;
										}

										&.is-active a {
											padding: 16px 35px 8px 40px;
											background: $whitecolor;
											color: #242424;
											margin-top: -6px;
										}
									}
								}
							}
						}
					}
				}
			}

			.RightWrap {
				display: inline-block;
				vertical-align: middle;
				float: right;
				margin: 32px 0px;
				width: 23%;
				ul {
					margin: 0;
					float: right;

					.header-quickSearch {
						line-height: 64px;
						border-right: 2px solid #dde2e4;
						padding-right: 25px;
						display: inline-block;
						position: relative;

						.navUser-action {
							padding: 0;

							&.navUser-action--quickSearch {
								width: 43px;
								height: 43px;
								border-radius: 21.5px;
								background-color: #3a3839;
								line-height: 43px;
								text-align: center;
								font-size: 0;
								display: inline-block;
								vertical-align: middle;
								z-index: 1;
								position: relative;

								&:after {
									content: "";
									font-size: 18px;
									font-weight: normal;
									color: $whitecolor;
									font-family: "Font Awesome 5 Free";
								}
							}
						}

						.d-quickSearch {
							position: absolute;
							top: 50%;
							transform: translateY(-50%);
							right: 15px;
							display: none;
							left: auto;

							.container {
								padding: 0px;

								form {
									margin: 0px;

									.form-field {
										margin: 0px;
										position: relative;

										.form-input {
											width: 680px;
											height: 64px;
											border-radius: 0px;
											border: 3px solid #231f20;
											font-size: 18px;
											line-height: 28px;
											padding: 17px 55px 17px 17px;

											&:focus {
												border-color: #4295f3;
											}
										}

										.search_btn {
											position: absolute;
											top: 10px;
											right: 10px;
											width: 43px;
											height: 43px;
											border-radius: 21.5px;
											background-color: #3a3839;
											line-height: 43px;
											text-align: center;
											display: inline-block;
											vertical-align: middle;
											font-size: 18px;
											font-weight: normal;
											font-family: "Font Awesome 5 Free";
											padding: 0px;
											border: 0px;
											color: #fff;
											display: none;
										}
									}
								}
							}
						}
					}

					.navUser-action--cart {
						width: 34px;
						height: 30px; 
						font-size: 0;
						padding: 0;
						margin: 12px 0 0 0px;
						background: url(../img/cart-header-retina.png) no-repeat center;
						background-size: auto 30px;
					}

					.navUser-item--checkout {
						margin: 15px 0 0 30px;

						.navUser-action {
							padding: 0 10px;
							height: 28px;
							background-color: #efa41f;
							font-size: 12px;
							font-weight: 800;
							line-height: 28px;
							letter-spacing: normal;
							color: $whitecolor;
							@include transition(all 0.5s ease);

							&:after {
								content: "";
								color: $whitecolor;
								font-family: "Font Awesome 5 Free";
								padding: 0 0 0 8px;
							}

							&:hover {
								background: #d99214;
								@include transition(all 0.5s ease);
							}
						}
					}
				}
			}
		}
	}
}

.ClearSearch {
	position: absolute;
    top: 7px;
    right: 50px;
    width: 30px;
    height: 30px;
    border-radius: 50%;
    background-color: #c3c9cb;
    line-height: 30px;
    text-align: center;
    display: inline-block;
    vertical-align: middle;
    font-size: 15px;
    font-weight: normal;
    font-family: "Font Awesome 5 Free";
    padding: 0px;
    border: 0px;
    color: #fff;
}

.quickSearchResults {
	margin-top: 0;
	position: fixed;
	left: 0;
	right: 0;
	width: auto;
	background: #fff;
	z-index: 9;
	top: 170px;
	@include transition(all 0.5s ease);
	overflow-y: auto;
	max-height: 600px;
	box-shadow: 0 10px 34px 0 rgba(0, 0, 0, 0.5);

	.modal-close {
		display: none;
	}

	h3 {
		text-align: center;
		font-size: 24px;
		font-weight: 900;
		line-height: 32px;
		color: #2b2b2b;
		text-transform: uppercase;
		margin: 0px;
	}

	.searchSuggestion {
		width: 100%;
		background: #f4f7f8;
		padding: 10px 0 0;
		ul {
			display: flex;
			width: 100%;
			margin: 0;
			padding: 0;
			list-style: none;
			li {
				flex-grow: 1;
				text-align: center;
				font-size: 18px;
				font-weight: normal;
				padding: 10px;
				color: #3a3839;
				margin: 0;
				&.ss-active {
					background: #fff;
					a {
						font-weight: bold;
						color: #0b4f68;
					}
				}
			}
		}
	}

	.searchInner {
		max-width: 1170px;
		width: 100%;
		margin: 0 auto;
		padding-top: 20px;

		>ul {
			margin: 0 -15px;
			padding: 0 0 15px;

			>li {
				width: 25%;
				float: left;
				padding: 15px;

				.card {
					border: 0px;
					margin: 0px;

					.Tire-Badge-outer,
					.Warning-Message {
						display: none;
					}

					.card-figure {
						border: solid 1px #d3d3d3;
						width: 100%;
						height: 237px;
						background: #fff;
						display: table;
						vertical-align: middle;

						a {
							display: table-cell;
							margin: 0 auto;
							width: 100%;
							position: relative;

							.card-img-container {
								img {
									max-height: 235px;
									display: block;
									margin: auto;
								}

								&:after {
									padding: 0px;
								}
							}
						}
					}

					.card-body {
						padding: 0;
						.card-body-left{
							display: block;
						    width: 100%;
						    .card-body-left-inner{
						    	float: left;
						    	width: 100%;
						    	margin: 0px;
						    }
						    .card-body-right-inner{
						    	width: 100%;
						    	padding: 0px;
						    }
						    
						}
						.card-title {
							font-size: 16px;
							font-weight: normal;
							line-height: 24px;
							margin: 11px 0 5px;
							height: 48px;
							overflow: hidden;
							display: block;
							order:2;

							a {
								color: #242424;
							}
						}
						.card-body-right{
							.card-text{
								display: block;
								.price-section{
									&:nth-child(3){
										max-width: 100%;
										float: left;
										text-align: left;
									}
								}
							}
							.LoginClubMSG{
								display: none;
							}
						} 
						.card-text,
						.product-sku,
						.Icon-Air-Pressure-Passed{
							display: none;
						}
						.price-section.price-section--saving,
						.four_p_price,
						.form-field.form-field--increments,
						.Last-Right,
						.price-section--withoutTax.rrp-price--withoutTax,
						.stock_level{
							display: none;
						}
					}

					.Bottom-Card {
						.Bottom-Card-Left {
							display: none;
						}

						.Bottom-Card-Right {
							.card-text {
								margin: 0px;

								.price-section.price-section--withoutTax {
									font-size: 24px;
									font-weight: 900;
									line-height: 32px;
									color: #1a9eca;
								}
							}
						}

						.Price-Per-Tire,
						.four_p_price,
						.form-field--increments,
						.FedxImg,
						.Same-Day-Ship-Btn {
							display: none;
						}

						.Tire-Specifications {
							display: none;
						}
					}

					.Bottom-Card-Last {
						display: none;
					}
				}
			}
		}

		.quickSearchBottom {
			width: 100%;
			float: left;
			border-top: 1px solid #d3d3d3;

			.quickSearchList {
				margin: 0 -18.5px;
				width: auto;

				>div {
					width: 33.33%;
					float: left;
					padding: 0 18.5px;

					h4 {
						font-size: 20px;
						font-weight: 900;
						line-height: 28px;
						color: #2b2b2b;
						text-transform: uppercase;
						margin: 30px 0 5px;
					}

					ul {
						list-style: none;
						margin: 0 -18.5px;

						li {
							width: 50%;
							float: left;
							padding: 0 18.5px;
							margin: 0 0 5px;

							a {
								font-size: 16px;
								font-weight: normal;
								line-height: 24px;
								padding: 3px 5px;
								color: #242424;
								display: inline-block;
							}
						}
					}
				}
			}
		}

		.quickbottomButton {
			float: left;
			width: 100%;
			padding: 30px 0;
			margin: 25px 0 0;
			border-top: 1px solid #d3d3d3;
			text-align: center;

			a {
				width: 254px;
				background: #fff;
				line-height: 20px;
				font-size: 18px;
				font-weight: 900;
				padding: 13px 0 11px;
				border-radius: 0px;
				display: inline-block;
				text-align: center;
				color: #3a3839;
				border: 1px solid #3a3839;
				text-transform: uppercase;
				&:hover{
					background: #ececec;
					@include transition(all 0.5s ease);
				}
			}

			p {
				font-size: 12px;
				font-weight: normal;
				line-height: 18px;
				display: block;
				margin: 15px 0 0;
				color: #242424;
				text-transform: uppercase;
			}
		}
	}
}

.sticky_active {
	.quickSearchResults {
		top: 69px;
		@include transition(all 0.3s ease);
	}
}

.custom_orderconfirmation .top-banner-winter-tires {
	display: none !important;
}

.top-banner-winter-tires {
	clear: both;
	position: relative;
	background: #00499e url(../img/top-banner-winter-tires.jpg);
	height: 80px;
	background-repeat: no-repeat;
	display: none;

	img {
		width: 100%;
		display: none;
	}

	.container {
		position: absolute;
		left: 0;
		right: 0;
		text-align: center;
		top: 50%;
		@include translate(0, -50%);

		.contentWrap {
			max-width: 676px;
			margin: 0 auto;
			display: inline-block;

			p,
			h3,
			a {
				margin: 0;
				display: inline-block;
				vertical-align: middle;
				color: $whitecolor;
				text-transform: uppercase;
			}

			p {
				font-size: 22.4px;
				font-weight: 800;
				line-height: 35px;
				letter-spacing: normal;
			}

			h3 {
				font-size: 40px;
				font-weight: 800;
				line-height: 48px;
				letter-spacing: normal;
				position: relative;
				margin: -15px 21px 0 29px;

				span {
					width: 164.8px;
					height: 14.6px;
					border-radius: 0px;
					background-color: $whitecolor;
					font-size: 11.2px;
					font-weight: 500;
					line-height: 14.4px;
					letter-spacing: normal;
					color: $deepbluecolor;
					display: inline-block;
					position: absolute;
					left: 0;
					bottom: -12px;
					text-transform: none;
				}
			}

			a {
				width: 104.8px;
				height: 32.8px;
				border-radius: 0px;
				border: solid 0.8px rgba(255, 255, 255, 0.5);
				font-size: 12px;
				font-weight: bold;
				line-height: 32px;
				letter-spacing: normal;
				text-align: center;
			}
		}

		.CloseSign {
			float: right;
			display: inline-block;
			vertical-align: middle;

			&:after {
				content: "\f00d";
				font-size: 20px;
				color: $whitecolor;
				font-family: "Font Awesome 5 Free";
			}
		}
	}
}

