/***********************************************/
/***********************************************/
/****************** SKELETONS ******************/
/***********************************************/
/***********************************************/

:root {
/*	--skeleton-gray: #dff1f9;
	--skeleton-bg-gray:#dff1f9;*/
	--skeleton-gray: #FBE7C2;
	--skeleton-bg-gray: #FBE7C2;
}


@keyframes blink-animation {
	0% {
		opacity: 0.4;
	}

	50% {
		opacity: 1;
	}

	100% {
		opacity: 0.4;
	}
}

.by-size-skeleton:after{
	content: "";
	position: relative;
	width: 100%;
	height:20px;
}

.by-size-skeleton  {
	background: white;
	position: relative;
	display: flex;
	align-content: flex-start;
	flex-wrap: wrap;
	justify-content: space-between;
	width:100%;
	max-width: 1170px; 
	margin-bottom: 20px;


	.size-wrap{
		animation: blink-animation 1.5s ease;
		animation-iteration-count: infinite;
		animation-fill-mode: forwards;
		opacity: 0.4;

		@for $i from 1 through 90 {
			&:nth-child(#{$i}n) {
				animation-delay: #{$i * 0.05}s;
			}
		}

		.size-box{
			width: 160px;
			height: 40px;
		/*	border-radius: 20px; */
			background-color: var(--skeleton-gray);
			margin-top:20px;
			margin-left: 15px;
			margin-right: 15px;
		}
	}
}
@media only screen and (max-width: 900px) {
	.by-size-skeleton  {
		background-color: var(--skeleton-bg-gray);
		justify-content: space-evenly;

		.size-wrap{
			.size-box{
				width: 100px;
				margin-top:15px;
				margin-left: 7.5px;
				margin-right: 7.5px;
			}

			@for $i from 31 through 90 {
				&:nth-child(#{$i}n) {
					display:none;
				}
			}
		}
	}
}

.clear-float{
	clear: both;
}

.shop-skeleton:after{
	content: "";
	position: relative;
	width: 100%;
	height:20px;
}


@keyframes shine-filters {
	0% {
		background-position: -100px;
	}
	40%, 100% {
		background-position: 300px;
	}
}

@keyframes shine-results {
	0% {
		background-position: -200px;
	}
	40%, 100% {
		background-position: 900px;
	}
}

.shop-skeleton  {
	width: 100%;

	*.mobile{
		display: none;
	}


	.shop-header-wrap{
		width: 100%;
		background-color: var(--skeleton-gray);
		min-height:90px;

		.shop-header{
			width:calc(100% - 30px);
			max-width: 1170px; 
			padding-top:7px;
			padding-bottom:7px;
			margin:auto;

			.shop-hd-left{
				float:left;
				width: 30%;
				min-width: 360px;
				.hd-box{
					float:left;
					height: 80px;
					min-width:110px;
					width:30%;
				/*	border-radius: 10px; */
					background-color: white;
				}
				.hd-line{
					float:left;
					height: 15px;
					width:25%;
					min-width: 100px;
				/*	border-radius: 10px; */
					background-color: white;
					margin-left: 19px;
					margin-top: 12px;

					&:nth-of-type(3){
						height: 20px;
						width:60%;
						min-width: 200px;
						margin-top: 10px;
					}
				}
			}
			.shop-hd-right{
				width: 26%;
				min-width: 280px;
				float:right;
				height: 55px;
			/*	border-radius: 10px; */
				background-color: white;
				margin-top: 12px;
				margin-right: 10px;
			}
		}
	}

	.results-wrapper{
		background: white;
		position: relative;
		display: flex;
		align-content: flex-start;
		flex-wrap: wrap;
		justify-content: space-between;
		width:calc(100% - 40px);
		max-width: 1170px; 
		margin-top: 30px;
		margin-bottom: 20px;
		margin-left:auto;
		margin-right:auto;


		.wrap-filters{
			background: white;
			width: 23%;
			max-width: 270px;

			.filter-box.filter-with-bubble{
				margin-top:50px;

				.filter-bubble{
					width:71px;
					height: 71px;
				/*	border-radius: 35.5px; */
					margin: auto;
					margin-top:-50px;
					background-color: white;
				}

				.filter-pretitle{
					width: 121px;
					height:18px;
					margin-top:17px;
					margin-bottom:17px;
					background-color: white;
				/*	border-radius: 10px; */
					margin-left: auto;
					margin-right: auto;
				}

				.filter-title{
					width: 240px !important;
					height:40px;
					margin-bottom:10px;
					background-color: white;
				/*	border-radius: 10px; */
					margin-left: auto;
					margin-right: auto;
				}
				.filter-subtitle{
					width: 150px !important;
					height:10px;
					margin-bottom:10px;
					background-color: white;
				/*	border-radius: 10px; */
					margin-left: auto;
					margin-right: auto;

					&.shorter{
						width: 110px !important;
					}
				}
			}

			.filter-box{
				width: 100%;
				padding: 15px;
				padding-top: 13px;
				margin-bottom:10px;
				background-color: var(--skeleton-gray);
			/*	border-radius: 4px; */

				animation: blink-animation 1.5s ease;
				animation-iteration-count: infinite;
				animation-fill-mode: forwards;
				opacity: 0.4;

				@for $i from 1 through 90 {
					&:nth-child(#{$i}n) {
						animation-delay: #{$i * 0.1}s;
					}
				}


                /*background-image: linear-gradient(90deg, var(--skeleton-gray) 0px, #e8e8e8 40px, var(--skeleton-gray) 80px);
                background-size: 600px;
                animation: shine-filters 2s infinite linear;*/

                .filter-title{
                	width: (100+random(120))+px;
                	height:22px;
                	background-color: white;
                /*	border-radius: 10px; */
                }

                .filter-subtitle{
                	width: (100+random(120))+px;
                	height:15px;
                	margin-top:15px;
                	background-color: white;
                /*	border-radius: 10px; */
                }

                @for $i from 1 through 10 {
                	&:nth-child(#{$i}) {
                		.filter-title{
                			width: (100+random(80))+px;
                		}
                		.filter-subtitle{
                			width: (100+random(90))+px;
                		} 

                		.filter-checks{
                			@for $i from 1 through 10 {
                				&:nth-child(#{$i}) {
                					.check-label{
                						@for $j from 1 through 10 {
                							&:nth-child(#{$j}) {
                								width: (90+random(70))+px;
                							}
                						}
                					}

                				}
                			}
                		}
                	}
                }

                .filter-checks{
                	display: block;
                	margin-top:15px;
                	.check-box{
                		float:left;
                		width: 28px;
                		height:28px;
                		background-color: white;
                	/*	border-radius: 10px; */
                	}
                	.check-label{
                		float:left;
                		width: (90+random(90))+px;
                		height:12px;
                		margin-left:10px;
                		background-color: white;
                	/*	border-radius: 10px; */
                		margin-top:10px;
                	}
                }
            }

        }
