/* IE Browser Popup */
.IEBPOP {
    position: absolute;
    top: 0;
    left: 0;
    background: rgba(0,0,0,.7);
    z-index: 999;
    right: 0;
    bottom: 0;
    display: none;
}
.iepopin {
    position: fixed;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    background: #fff;
    padding: 70px 59px;
    width: 100%;
    max-width: 941px;
    text-align: center;
}

ul.Browser-list{
    margin: 0px -30px;
    float: none;
    list-style: none;
    font-size: 0;
    text-align: center;
}

ul.Browser-list:after{
    content: "";
    display: block;
    clear: both;
}
ul.Browser-list li{
    display: inline-block;
    vertical-align: middle;
    padding: 0px 30px;
}
ul.Browser-list li p.B-list-name a{
    font-size: 18px;
    line-height: 24px;
    color: #232323;
    font-weight: 700;
    text-transform: uppercase;
    padding: 12px 22px 11px 22px;
    border: 1px solid #3a3839;
    display: inline-block;
    transition: all .5s ease;
}
ul.Browser-list li p.B-list-name a:hover {
    background: #ececec;
    transition: all .5s ease;
}
ul.Browser-list li .B-list-img{
    margin: 0px 0px 24px;
    height: 114px;
}
ul.Browser-list li p.B-list-name{
    margin: 0px;
}

.iepopin > h2{
    font-size: 36px;
    font-weight: 800;
    text-transform: uppercase;
    color: #2b2b2b;
    line-height: 42px;
    margin: 0px 0px 15px;
}
.iepopin h3{
    font-size: 25px;
    line-height: 28px;
    color: #3a3839;
    font-weight: 700;
    margin: 0px 0px 30px;
}
.iepopin > p{
    font-size: 18px;
    line-height: 28px;
    margin: 0px 0px 30px;
    font-weight: 400;
    color: #3a3839;
}
ul.Browser-list li .B-list-img img{
    display: block;
    margin: 0 auto;
}
@media only screen and (max-width: 900px){
    .iepopin{
        max-width: 95%;
        padding: 50px 50px;
    }
    .iepopin>h2 {
        font-size: 28px;
        line-height: 35px;
    }
    .iepopin h3{
        margin: 0px 0px 20px;
    }
    ul.Browser-list{
        margin: 0px -20px;
    }
    ul.Browser-list li{
        width: 33.33%;
        padding: 0px 20px;
    }
    ul.Browser-list li p.B-list-name a{
        font-size: 15px;
        padding: 10px 9px;
        width: 100%;
    }
}
@media screen and (max-width: 830px) and (min-width: 800px){
    .iepopin{
        max-height: 90%;
        overflow-y: auto;
    }
}
@media only screen and (max-width: 767px){
    .iepopin{
        max-height: 90%;
        overflow-y: auto;
        padding: 30px;
    }
    ul.Browser-list{
        margin: 0px -10px;
    }
    ul.Browser-list li{
        padding: 0px 10px;
    }
    ul.Browser-list li p.B-list-name a{
        font-size: 14px;
        padding: 6px 0px;
    }
   .iepopin > p{
       font-size: 16px;
       margin: 0 0 20px;
   }
   .iepopin h3{
        font-size: 20px;
        line-height: 28px;
        margin: 0px 0px 10px;
   }
   .iepopin>h2 {
        font-size: 22px;
        line-height: 28px;
        margin: 0px 0px 10px;
    }
    ul.Browser-list li .B-list-img{
        height: 75px;
        margin: 0px 0px 18px;
    }
    ul.Browser-list li .B-list-img img{
        max-height: 75px;
    }
    .blog .blog-post-body .blog-header .blog-title h1.Post_Heading{
        line-height: 24px;
    }
}
@media only screen and (max-width: 479px){
    .iepopin{
        padding: 20px;
    }
    .iepopin>h2 {
        font-size: 18px;
        line-height: 25px;
    }
    .iepopin h3 {
        font-size: 16px;
        line-height: 20px;
    }
    .iepopin>p{
        font-size: 15px;
    }
    ul.Browser-list li{
        width: 100%;
        margin: 0px 0px 20px;
    }
    ul.Browser-list li:last-child{
        margin: 0px;
    }
}
