product.scss:
.custom_product .body > .container{
    max-width: 100%;
    padding: 0px;
}
.custom_product .CategoryTopContent{
    background:#f4f7f8;
    float: left;
    width: 100%;
    padding: 20px 0px;
}
.custom_product .CategoryTopContent p{
    font-size: 20px;
    font-weight: 800;
    color: #3a3839;
    line-height: 28px;
    margin: 11px 0px;
    display: inline-block;
}
.custom_product .CategoryTopContent .Left{     
    float: left;
    width: 100%; 
    max-width: calc(100% - 282px);
} 
.custom_product .CategoryTopContent .Right{  
    float: right;
    width: auto;
}
.custom_product .CategoryTopContent .Right a{
    font-size: 16px;
    font-weight: 800;
    background: #efa41f;
    color: #fff;
    text-transform: uppercase;
    width: 277px;
    text-align: center;
    padding: 15px 0px 13px;
    line-height: 22px;
    display: inline-block;
    border-radius: 0px;
}
.custom_product .CategoryTopContent .Right a:hover{
    background: #d99214;
    transition: all 0.5s ease; 
}
.custom_product .ProWrap{
    width: 100%;
    float: left;
    margin: 30px 0px 0px;
}
.VehicleTireFitWrap {
    float: left;
    width: 100%;
    background: #f6f6f6;
    margin: 15px 0 15px;
    padding:0px;
}
.VehicleTireFitWrapInner {
    font-size: 0px;
    width: 100%;
    float: left;
    margin:0px;
    padding: 30px 0px;
}
.VehicleTireFitWrap .VehicleTireFitWrapInner h3 {
    font-size: 26px;
    line-height: 36px;
    font-weight: 800;
    color: #2b2b2b;
    margin: 0 20px 0px 0px;
    display: inline-block;
    vertical-align: middle;
}
.VehicleTireFitWrap .VehicleTireFitWrapInner a.Btn.Fits {
    font-size: 13px;
    line-height: 17px;
    background: #fff;
    font-weight: 700;
    border: 0.8px solid #2b2b2b;
    padding: 9px 21px 9px 23px;
    display: inline-block;
    vertical-align: middle;
    color: #272727;
}
.VehicleTireFitWrap .VehicleTireFitWrapInner a.Btn.Fits:hover{
    background: #ececec;
    color: #272727;
    transition: all 0.5s ease;
}
.VehicleTireFitWrapInner.form.is-open{
    display: block;
}
.VehicleTireFitWrapInner.form{
    padding: 12px 0px;
    display: none;
}
.VehicleTireFitWrapInner.form h4 {
    margin: 0px 0px 3px;
    font-weight: 800;
    color: #2b2b2b;
    text-transform: uppercase;
    line-height: 28px;
    font-size: 20px;
}
.TireFIt {
    float: left;
    width: 100%;
}
.TireFIt p {
    background: #f6f6f6;
    margin: 0;
    padding: 6px 0px;
    text-align: center;
}
.TireFIt p.Not span {
    color: #f10023;
}
.TireFIt p span {
    font-size: 16px;
    line-height: 18px;
    vertical-align: middle;
    display: inline-block;
    font-weight: 700;
    margin-right: 20px;
}
.TireFIt p a {
    border: solid 1px rgba(102, 102, 102, 0.48);
    display: inline-block;
    vertical-align: middle;
    font-size: 11px;
    line-height: 18px;
    color: #666666;
    font-weight: 700;
    text-transform: uppercase;
    padding: 4px 10px;
}
.TireFIt p span {
    font-size: 16px;
    line-height: 18px;
    vertical-align: middle;
    display: inline-block;
    font-weight: 700;
    margin-right: 20px;
    color: #6cac22;
}
.TireFIt p.Not span {
    color: #f10023;
}
.TireFIt p span:before{
    content: "";
    font-family: "Font Awesome 5 Free";
    font-size: 18px;
    margin-right: 10px;
    display: inline-block;
    vertical-align: top;
}
.TireFIt p.Not span:before {
    content: "\f071";
    font-size: 18px;
    margin-right: 7px;
}
.custom_product .productView{
    margin: 0 -15px 52px;
    padding: 0px;
}
.custom_product .productView-images{
    width: 33.334%;
    float: left;
    position: relative;
    padding: 0 15px;
    margin: 0px;
}
.custom_product .productView-images .productView-image{
    border: solid 1px #d3d3d3;
    height: 370px;
    width: 100%;
    position: static;
}
.custom_product .productView-images .productView-img-container{
    margin: 0px;
}
.custom_product .productView-images .productView-img-container:after{
    display: none;
}
.custom_product .productView-images .productView-image .productView-img-container img{
    max-height: 368px;
}
.custom_product .Sale-Badge{
    position: absolute;
    top:1px;
    left: 16px;
    font-size: 16px;
    font-weight: 800;
    border-radius:0px 0px 3px 0px;
    background-color: #f34242;
    display: inline-block;
    line-height: 24px;
    text-transform: uppercase;
    padding:5px 10px;
    color: #fff;
    z-index: 1;
}
.custom_product .ProRight{
    width: 66.66%;
    float: right;
    padding: 0 15px;
    margin: 0px;
}
.custom_product .ProRight .productView-details{
    width: 100%;
    padding:0px;
    margin: 0 0 15px;
}
.productView-image + .productView-thumbnails{
    margin: 15px -4px 0px;
}
.productView-image + .productView-thumbnails .productView-thumbnail{
    width: 20%;
    float: left;
    padding: 0 4px;
    height: 75px;
    display: table;
}
.productView-thumbnails .slick-list .slick-track{
    margin: 0px;
}
.productView-image + .productView-thumbnails .productView-thumbnail .productView-thumbnail-link{
    width: 100%;
    display: table-cell;
    vertical-align: middle;
    max-width: 100%;
    max-height: 100%;
    padding: 0px;
    height: 100%;
    border:2px solid #d3d3d3;
    opacity: 0.5;
}
.productView-image + .productView-thumbnails .productView-thumbnail .productView-thumbnail-link.is-active{
    opacity: 1;
}
.productView-thumbnails .slick-list{
    margin: 0px;
}
.productView-thumbnails .slick-dots{
    bottom: 0px;
}
.productView-thumbnails .slick-dots li, 
.productView-thumbnails .slick-dots li button, 
.productView-thumbnails .slick-dots li button:before{
    margin: 0 3px;
    height: 12px;
    width: 12px;
    padding: 0px;
}
.productView-thumbnails .slick-dots li button,
.productView-thumbnails .slick-dots li button:before{
    margin: 0px;
}
.productView-thumbnails .slick-dots li button:before{
    border:1px solid #efa41f;
}
.productView-thumbnails .slick-dots li.slick-active button:before,
.productView-thumbnails .slick-dots li button:hover:before{
    border:1px solid #efa41f;
    background:#efa41f;
}
.productView-thumbnails .productView-thumbnail img{
    position: static;
    max-height: 73px;
    max-width: 100%;
}
.ProImageTxt{
    float: left;
    width: 100%;
    margin: 15px 0 0;
}
.ProImageTxt p{ 
    font-weight: normal;
    font-size: 16px;
    color: #242424;
    line-height: 20px;
    margin: 0 0 8px; 
}
.ProImageTxt p b{
    font-weight: 700;
    text-transform: uppercase;
}
.productView-image .easyzoom-flyout{
    left: 100%;
    z-index: 1;
    overflow: hidden;
    top: 0;
    background: #fff;
    box-shadow: 0px 2px 12px #000;
    height: 600px;
    width: 650px; 
}
.custom_product .ProRight .productView-product{
    position: relative;
    padding: 0px;
    float: left;
    width: 100%;
    border:0px;
}
.productView-product .Tire-Badge-outer {
    position: absolute;
    top: -3px;
    max-width: 100%;
    left: 0px;
    right: 0px;
    margin: 0 auto;
    width: auto;
    text-align: center;
    border-top: 3px solid #62d7ff;
    border-radius: 0px;
}
.productView-product .Tire-Badge-outer.Prod_new{
    border-color: #1aba8d;
}
.productView-product .Tire-Badge-outer.Prod_new span{
    background:#1aba8d;
}   
.productView-product .Tire-Badge-outer.Prod_take_off{
    border-color: #18a4b2;
}
.productView-product .Tire-Badge-outer.Prod_take_off span{
    background:#18a4b2;
}
.productView-product .Tire-Badge-outer span {
    display: inline-block;
    padding: 3px 12px;
    font-size: 16px;
    color: #fff;
    text-transform: uppercase;
    font-weight: 800;
    background: #62d7ff;
    border-radius: 0px 0px 10px 10px;
}
.productView-product .productView-brand{
    font-size: 20px;
    font-weight: 500;
    color: #242424;
    line-height: 24px;
    margin: 0px 0px 5px;
    display: inline-block;
}
.productView-product .productView-brand a{ 
    color: #2b2b2b;
    pointer-events: none;
}
.productView-product .productView-rating {
    position: static;
    right: 0;
    text-align: left;
}
.yotpo .yotpo-bottomline{ 
    display: block !important; 
}
.yotpo .yotpo-bottomline .yotpo-icon-empty-star{
    color: #d3d3d3 !important; 
}
.productView-product .productView-rating .productView-reviewLink, 
.yotpo .write-review-btn-hidden{
    display: inline-block !important;
    margin: 0px !important;
    text-transform: uppercase;
    font-size: 12px !important;
    line-height: 18px !important;
    color: #242424 !important;
    font-family: "multi-display" !important;
}
.productView-product .productView-title{
    font-size: 30px;
    font-weight: 800;
    line-height: 40px;
    margin: 0 0 10px;
    text-transform: inherit;
    letter-spacing: normal;
    color: #2b2b2b;
    max-width: 370px;
}
.productView-product .product-sku{
    font-size: 19px;
    font-weight: 700;
    line-height: 28px;
    color: #3a3839;
    margin: 0px 0px 12px;
    display: inline-block;
    width: 100%; 
}
.productView-product .BelowProductName .ProTotalWrap{
    float: right;
    width: auto;
    clear: right;
    margin: 0px 0px 15px;
}
.productView-product .BelowProductName{
    float: left;
    width: 100%;
    margin: 0px;
}
.productView-product .BelowProductName .Left{
    float: left;
    width: 100%;
}
.productView-product .BelowProductName .Right{
    float: right;
    width: 51%;
    text-align: right;
}
.productView-product .BelowProductName .Right .ProLogMemberinner{
    border-radius: 4.7px;
    border: solid 3px #fbe7c2;
    float: left;
    width: 100%;
}
.productView-product .BelowProductName .Right .ProLogMemberinner .LoginClubMSG{
    border-radius: 0px 0px 3.7px 3.7px;
    background: #fbe7c2;
    float: left;
    width: 100%;
}
.productView-product .BelowProductName .Right .ProLogMemberinner .LoginClubMSGOuter{
    padding: 14px 14px 6px;
    float: left;
    width: 100%;
}
.ProLogMemberinner .LoginClubMSG h4{
    font-size: 14.9px;
    font-weight: 800;
    max-width: 193px;
    margin: 10px auto 10.8px;
    line-height: 18.6px;
    text-align: center;
}
.ProLogMemberinner .LoginClubMSG h4 a{
    color: #3a3839;
    text-decoration: underline;
}
.ProLogMemberinner .LoginClubMSG h4 a:hover{
    color: #3a3839;
    text-decoration: none;
}

.productView-product .Price-Per-Tire {
    font-size: 16px;
    font-weight: 800;
    color: #242424;
    line-height: 24px;
    display: inline-block;
}
.productView-product .form-field--increments{
    margin: 0px;
}
.productView-product .form-field--increments .form-increment {
    max-width: 150px;
    border: solid 1px #d3d3d3;
    border-radius: 100px;
    display: inline-block;
    vertical-align: middle;
}
.productView-product .form-field--increments .form-increment .button {
    width: 33.33%;
    float: left;
    height: 48px;
    background: #f4f7f8;
    border: 0px;
    border-radius: 100px 0px 0px 100px;
}
.productView-product .form-field--increments .form-increment .button i {
    font-size: 18px;
    font-family: "Font Awesome 5 Free";
    font-style: normal;
    color: #5a5a5a;
    font-weight: normal;
}
.productView-product .form-field--increments .form-increment .form-input {
    width: 33.33%;
    float: left;
    height: 48px;
    border-left: solid 1px #d3d3d3;
    border-right: solid 1px #d3d3d3;
    border-radius: 0px;
    font-size: 20px;
    font-weight: bold;
    line-height: 26px;
    color: #313131;
}
.productView-product .form-field--increments .form-increment .form-input + .button {
    border-radius: 0px 100px 100px 0px;
}
.productView-product .BelowProductName .Right .stock_level {
    font-size: 12.1px;
    font-weight: normal;
    color: #3a3839;
    display:block;
    vertical-align: middle;
    margin:10px 0px 0px;
    text-align: left;
    line-height: 22.3px;
    float: left;
    width: 100%;
}
.productView-product .BelowProductName .Right .stock_level:before {
    content: "";
    font-size: 14.9px;
    font-family: "Font Awesome 5 Free";
    margin-right: 10px;
    color: #ff6b07;
    vertical-align: middle;
}
.productView-product .BelowProductName .Right .stock_level.In-Stock-Btn:before {
    content: "";
    color: #6cac22;
    font-size: 14.9px;
}
.productView-product .BelowProductName .Icon-Air-Pressure-Passed{
    margin: 28px 0px 10px;
    width: 100%;
}
.productView-product .BelowProductName .Icon-Air-Pressure-Passed .Air-Pressure{
    line-height: 56px;
    margin: 0px;
}
.productView-product .BelowProductName .Bottom-Card-Left{
    float: left;
    width: 100%;
    border-top: 1px solid #d5dcde;
    padding: 15px 0px 20px;
}
.productView-product .BelowProductName .Bottom-Card-Left ul{
    list-style: none;
    margin: 0px;
}
.productView-product .BelowProductName .Bottom-Card-Left ul li {
    margin: 0 0 10px;
    display: inline-block;
    width: 100%;
}
.productView-product .BelowProductName .Bottom-Card-Left ul li:empty {
    display: none;
}
.productView-product .BelowProductName .Bottom-Card-Left ul li ul li{
     margin: 0 0 -4px;
}
.productView-product .BelowProductName .Bottom-Card-Left ul li img {
    max-width: 30px;
    display: inline-block;
    vertical-align: middle;
}
.productView-product .BelowProductName .Bottom-Card-Left ul li span {
    font-size: 14px;
    font-weight: normal;
    color: #242424;
    line-height: 20px;
    padding: 0px;
    display: inline-block;
    vertical-align: middle;
}
.productView-product .BelowProductName .Bottom-Card-Left ul li.Tread-Depth span {
    font-weight: 800;
    text-transform: uppercase;
    font-size: 20px;
    line-height: 28px;
}
.productView-product .BelowProductName .Bottom-Card-Left ul li.Tread-Life {
    background: #1a9eca;
    font-size: 12px;
    font-weight: 800;
    color: #fff;
    text-transform: uppercase;
    padding: 1px 5px;
    clear: left;
    border-radius: 2px;
    margin: 0px 0px 5px;
    width: auto;
}
.productView-product .BelowProductName .ProTotalWrap .Top{
    padding: 0px;
    color: #3a3839;
    font-size: 18px;
    font-weight: normal;
    float: left;
    width: 100%;
}
.productView-product .BelowProductName .four_p_price{
    display: inline-block;
    line-height: normal;
    vertical-align: middle;
    font-size: 18px;
    line-height: 26px;
    color: #3a3839;
    text-align: right;
    margin: -11px 0px 14px;
    max-width: 100%;
    width: 100%;
    float: right;
    clear: right;
}
.productView-product .BelowProductName .four_p_price span{
    font-size: 18px;
    font-weight: normal;
    line-height: 28px;
    color: #242424;
    float: none;
}
.productView-product .BelowProductName .four_p_price span:last-child{
    display: inline-block;
    vertical-align: middle;
    font-weight: normal;
    margin-left: 0px;
}
.productView-product .BelowProductName .ProTotalWrap .Bottom {
    border:1px solid #d5dcde;
    border-radius: 0px 0px 4px 4px;
    padding: 13px 15px;
    float: left;
    width: 100%;
    background:#f0f5f7;
}
.productView-product .BelowProductName .ProTotalWrap .Bottom .FedxImg,
.productView-product .BelowProductName .ProTotalWrap .Bottom .Same-Day-Ship-Btn{
    display: inline-block;
    width: auto;
    vertical-align: middle;
}
.productView-product .BelowProductName .ProTotalWrap .Bottom .FedxImg{
    margin: 0 17px 0px 0px;
}
.productView-product .BelowProductName .ProTotalWrap .Bottom .Same-Day-Ship-Btn{
    font-size: 14px;
    font-weight: bold;
    color:#242424;
    line-height: 20px;
    position: relative;
    padding: 0 0 0 30px;
}
.productView-product .BelowProductName .ProTotalWrap .Bottom .ProSubtotal{
    float: right;
    padding: 9px 0px 7px;
}
.productView-product .BelowProductName .ProTotalWrap .Bottom .ProSubtotal span{
    font-size: 18px;
    font-weight: normal;
    line-height: 28px;
    color:#242424;
    margin:0px;
    display: inline-block;
    vertical-align: middle;
}
.productView-product .BelowProductName .ProTotalWrap .Bottom .ProSubtotal span.SubtotalVal{
    font-size: 24px;
    font-weight: 800;
    line-height: 32px;
    margin: 0px 0px 0px 7px;
    color: #1a9eca;
}
.productView-product .BelowProductName .ProTotalWrap .Bottom .Same-Day-Ship-Btn:before{
    content: "";
    display: inline-block;
    font-size: 16px;
    font-family: "Font Awesome 5 Free";
    position: absolute;
    left: 0px;
    top:10px;
}
.custom_product .ProRight .productView-details .productView-options{
    margin: 0px;
    width: 100%;
    float: left;
}
.custom_product .productView-product .BelowProductName .four_p_price{
    margin: -9px 0px 10px;
}
.custom_product .productView-price.AfterLogin .price-section:nth-child(3){
    text-align: right;
}
.custom_product .productView-product .BelowProductName .AfterLogin .four_p_price{
    width: auto;
    margin: 3px 0px 10px;
}
.custom_product .ProRight .productView-details .productView-options label.form-label.form-label--inlineSmall{
    display: none;
}
.custom_product .ProRight .productView-details .productView-options label.form-label{
    background: #f6f6f6;
    padding:12px 1px 12px 49px;
    font-weight: 700;
    border:1px solid #d3d3d3;
    font-size: 13px;
    color: #3a3839;
}
.custom_product .ProRight .productView-details .productView-options label.form-label:before{
    border-color: #3a3839;
    height: 24px;
    width: 24px;
    background: #fff;
    top: 15px;
    border-radius: 2px;
    left: 10px;
}
.custom_product .ProRight .productView-details .productView-options .form-checkbox + .form-label::after{
    background: url(https://dev-prioritytire.mybigcommerce.com/content/checked-retina.png) center no-repeat;
    top: 20px;
    margin: auto;
    left: 15px;
    background-size: auto 16px;
}
.productView-options .form-action{
    margin: 0px;
    float: right;
    width: auto;
    padding: 0px;
}
.productView-product .Free-Road-Hazard-Result{
    margin: 3px 0 0;
}
.productView-options .form-action #form-action-addToCart{
    background: url(https://www.bestusedtires.com/content/icon-cart-retina.png) 14% 50% no-repeat #efa41f;
    background-size: auto 24px;
    border-radius: 0px;
    font-size: 20.5px;
    font-weight: 800;
    color:#fff;
    text-transform: uppercase;
    line-height: 26px;
    padding: 15px 22px 15px 70px;
    width: 231px;
    display: inline-block;
    margin: 0px;
    border:0px;
    text-align: left;
}
.productView-options .form-action #form-action-addToCart:hover{
    background: url(https://www.bestusedtires.com/content/icon-cart-retina.png) 14% 50% no-repeat #d99214;
    background-size: auto 24px;
    transition: all 0.5s ease; 
}

.custom_product .ProRight .productView-details .Risk-Paypal-Img{
    float: right;
    margin: 0 30px 0px 0px;
}
.custom_product .ProRight .productView-details .Risk-Paypal-Img img{
    max-width: 304px;
}
.custom_product .ProStickyBar {
    float: left;
    width: 100%;
    box-shadow: 0 0 12px 0 rgba(0, 0, 0, 0.5);
    position: relative;
    margin: 0px;
    z-index: 1;
}
.custom_product .ProStickyBar:before {
    content: "";
    background: #fff;
    width: 100%;
    position: absolute;
    height: 17px;
    top: -17px;
}
.custom_product .ProStickyBar ul{
    list-style: none;
    margin: 0px;
}
.custom_product .ProStickyBar ul li{
    float: left;
    width: auto;
    margin: 0px 30px 0px 0px;
    padding: 23px 0px;
}
.custom_product .ProStickyBar ul li.is-active {
    position: relative;
}
.custom_product .ProStickyBar ul li.is-active a {
    color: #4295f3;
}
.custom_product .ProStickyBar ul li.is-active:before {
    content: "";
    background: #4295f3;
    position: absolute;
    left: 0;
    right: 0;
    width: auto;
    height: 4px;
    bottom: 0px;
}
.custom_product .ProStickyBar ul li a{
    font-size: 16px;
    font-weight: 700;
    line-height: 24px;
    text-transform: uppercase;
    color: #3a3839;
}
.custom_product .SpecificationsDetails,
.ProSpecifications,
.ProDescription,
.ProReviews,
.ProShipping,
.ProNewUsed,
.ProCompatible,
.ProductDisclosure,
.SimilarTires,
.RecentlyViewed{
    width: 100%;
    float: left;
}

.SpecificationsDetails .SpecificationsList{
    background:#f6f6f6;
    float: left;
    width: 100%;
    border-radius: 0px;
    padding: 30px 0px;
