.SwitchSizePop {
    position: absolute;
    top: 115px;
    left: 0;
    right: 0;
    background: #f6f6f6;
    box-shadow: 0 8px 15px 0 rgba(0, 0, 0, 0.15);
    z-index: 10;
    padding: 30px 0 15px;

    .inner {
        margin: 0 auto;
        max-width: 1170px;
        padding: 0px;

        .Top {
            width: 100%;
            float: left;

            .Left {
                margin: 22px 0 0;
                float: left;
                width: auto;
                h3 {
                    font-size: 20px;
                    font-weight: 800;
                    color: #2b2b2b;
                    line-height: 28px;
                    margin: 0;
                    display: inline-block;
                }

                a {
                    display: inline-block;
                    margin-left: 12px;
                    background: #ffffff;
                    font-size: 12px;
                    line-height: 20px;
                    border-radius: 0px;
                    border: solid 1px #d3d3d3;
                    color: #242424;
                    padding: 4px 0px;
                    width: 151px;
                    text-align: center;
                    font-weight: 800;
                }
            }

            .Right {
                padding: 0;

                a {
                    font-size: 16px;
                    font-weight: 800;
                    background: #efa41f;
                    font-size: 16px;
                    color: #ffffff;
                    line-height: 22px;
                    border-radius: 0px;
                    width: 304px;
                    display: inline-block;
                    text-align: center;
                    text-transform: uppercase;
                    padding: 15px 0 13px;
                }
            }
        }

        .SwitchSizePopContent {
            width: 100%;
            float: left;
            padding: 5px 0 0;

            ul {
                margin: 0 -15px;
                list-style: none;

                li {
                    width: $threecolumn;
                    float: left;
                    padding: 10px;

                    .Vehicle_inner {
                        border: 1px solid #d5dcde;
                        padding: 10px;
                        border-radius: 0px;
                        width: 100%;
                        float: left;
                        background: #fff;

                        div {
                            display: inline-block;
                            vertical-align: middle;
                            width: 25%;
                            float: left;

                            &.Vehicle-Recent-Content {
                                width: 75%;
                                vertical-align: top;

                                p {
                                    margin: 0px;
                                    font-size: 14px;
                                    font-weight: 700;
                                    color: #3a3839;
                                    line-height: 20px;
                                    display: inline-block;
                                    text-transform: uppercase;
                                    margin-left: 15px;
                                }

                                a {
                                    float: right;
                                    margin: 0px;
                                    width: 67px;
                                    padding: 10px 0px;
                                    border-radius: 0px;
                                    font-size: 14px;
                                    font-weight: 800;
                                    color: #fff;
                                    line-height: 20px;
                                    background: #efa41f;
                                    border: 0px;
                                    text-transform: uppercase;
                                }
                            }
                        }
                    }

                    .size_inner {
                        border: 1px solid #d5dcde;
                        padding: 10px;
                        border-radius: 0px;
                        width: 100%;
                        float: left;
                        background: #fff;

                        div {
                            display: inline-block;
                            vertical-align: middle;
                            float: left;

                            &.Size-Recent-Img {
                                width: 25%;
                            }

                            &.Size-Recent-Content {
                                width: 75%;
                                vertical-align: top;

                                .Front-Rear-Text {
                                    width: 72%;
                                    display: inline-block;
                                    padding-left: 15px;

                                    span {
                                        margin: 0px;
                                        font-size: 14px;
                                        font-weight: 800;
                                        color: #242424;
                                        line-height: 20px;
                                        display: inline-block;
                                        text-transform: uppercase;
                                        margin: 0px;
                                        float: left;

                                        &.Front-Label,
                                        &.Rear-Label {
                                            font-size: 12px;
                                            font-weight: normal;
                                            margin-right: 3px;
                                        }

                                        &:nth-child(3) {
                                            clear: left;
                                        }
                                    }
                                }

                                .Tire-Size-Text {
                                    width: 72%;
                                    display: inline-block;
                                    padding-left: 15px;

                                    span {
                                        margin: 0px;
                                        font-size: 14px;
                                        font-weight: 800;
                                        color: #3a3839;
                                        line-height: 20px;
                                        display: inline-block;
                                        text-transform: uppercase;
                                        margin: 0px;
                                        float: left;

                                        &.Tire-Size-Label {
                                            font-size: 12px;
                                            font-weight: normal;
                                            margin-right: 3px;
                                        }

                                        &:last-child {
                                            clear: left;
                                        }
                                    }
                                }

                                a {
                                    float: right;
                                    margin: 0px;
                                    width: 67px;
                                    padding: 10px 0px;
                                    border-radius: 0px;
                                    font-size: 14px;
                                    font-weight: 700;
                                    color: #fff;
                                    line-height: 20px;
                                    background: #efa41f; 
                                    border: 0px;
                                    text-transform: uppercase;
                                }
                            }
                        }
                    }
                }
            }
        }
    }
}
