components/account/messages-form:
{{#if customer.inbox.orders}}
    <h2>{{lang 'forms.inbox.send_message'}}</h2>
    <form class="form" data-inbox-form action="{{urls.account.send_message}}" method="post">
        {{inject 'enterOrderNum' (lang 'forms.validate.account.inbox.order')}}
        {{inject 'enterSubject' (lang 'forms.validate.account.inbox.subject')}}
        {{inject 'enterMessage' (lang 'forms.validate.account.inbox.message')}}
        <div class="form-field">
            <label class="form-label" for="message_order_id">
                {{lang 'forms.inbox.order' }}
                <small>{{lang 'common.required' }}</small>
            </label>
            <select class="form-select" name="message_order_id" id="message_order_id">
                {{#each customer.inbox.orders}}
                    <option value="{{id}}">{{lang 'forms.inbox.order_display' id=id date=date total=amount.formatted}}</option>
                {{/each}}
            </select>
        </div>
        <div class="form-field">
            <label class="form-label" for="message_subject">
                {{lang 'forms.inbox.subject' }}
                <small>{{lang 'common.required' }}</small>
            </label>
            <input type="text" class="form-input" name="message_subject" id="message_subject">
        </div>
        <div class="form-field">
            <label class="form-label" for="message_content">
                {{lang 'forms.inbox.message' }}
                <small>{{lang 'common.required' }}</small>
            </label>

            <textarea class="form-input" name="message_content" id="message_content" rows="7" ></textarea>
        </div>
        <div class="form-actions">
            <input class="button button--primary" type="submit" value="{{lang 'forms.inbox.submit_value' }}">
            <input class="button" type="reset" value="{{lang 'forms.inbox.clear_value' }}">
        </div>
    </form>
{{else}}
    {{> components/common/alert-info (lang 'forms.inbox.no_orders')}}
{{/if}}

components/common/social-links:
{{#if social_media}}
    <ul class="socialLinks socialLinks--alt">
        {{#each social_media}}
            <li class="socialLinks-item">{{#if display_name '===' 'google+'}}<a class="icon icon--google" href="{{url}}" target="_blank"><svg><use xlink:href="#icon-google" /></svg></a>
            {{else if display_name '===' 'facebook'}}<a class="icon icon--{{display_name}}" href="{{url}}" target="_blank"><i class="fab fa-facebook-f"></i></a>
            {{else}}<a class="icon icon--{{display_name}}" href="{{url}}" target="_blank"><i class="fab fa-{{display_name}}"></i></a>                  
            {{/if}}</li>
        {{/each}}
    </ul>
{{/if}}

pages/account/inbox:
{{#partial "page"}}

{{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}
<h1 class="page-heading Account-Heading">{{lang 'account.messages.heading' }}</h1>
{{> components/account/navigation account_page='messages'}}

<main class="account account--fixed">
    <div class="account-body">
        <section class="account-content">
            {{#if forms.inbox.error}}
                {{> components/common/alert-error forms.inbox.error}}
            {{/if}}
            {{#if forms.inbox.success}}
                {{> components/common/alert-success forms.inbox.success}}
            {{/if}}
            {{> components/account/messages-list messages=customer.inbox.messages}}
            {{> components/account/messages-form}}
        </section>
    </div>
</main>
<style>.footer{margin:0px;}</style>
{{/partial}}
{{> layout/base}}
